import { useState } from "react";

const Tasks = () => {
    const [tasks, setTasks] = useState([
        {
            id: 1,
            text: 'Doctors Appointment',
            day: 'Feb 15',
            reminder: true,
        },
        {
            id: 2,
            text: 'Meeting Appointment',
            day: 'Dec 15',
            reminder: true,
        },
        {
            id: 1,
            text: 'Food Shopping',
            day: 'Feb 15',
            reminder: false,
        }
    ])

    return (
        <>
            {tasks.map((task) => (
                <h3 key={task.id}>{task.text}</h3>
            ))}
        </>
    );
};

export default Tasks;
